# -*- coding: utf-8 -*-import requestsURL_Records=[             'http://www.google.com',             'http://www.yahoo.com',             'http://beng.com',             'http://www.masrawy.com'             ]try:    for row in URL_Records:        r = requests.get(str(row))        if(r.status_code != 200):            print('Error status code '+str(r.status_code))        else:            print('Success ')except Exception as e:    print ("Fail!")    print (str(e))print ("*************************Record Done!********************************" )